workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class BusinessPermitSummaryController
{
    @Service("ReportParameterService")
    def svcParams;

    @Service('BPReportService') 
    def svc 

    @Service('DateService')
    def dateSvc

    def year
    def data

    void init() {
        year = dateSvc.serverYear 
    }

    def preview() {
        generateReport()
        return 'preview' 
    }

    void generateReport() {
        data = svc.generateBusinessPermitSummary( year )
        report.viewReport()
    }

    void print(){
        generateReport()
        ReportUtil.print( report.report, true )
    }

    def report = [
        getReportName : { 'etracs2/bp/report/businesspermitsummary/businesspermitsummary.jasper' },
        getReportData : { return data },
        getParameters : { svcParams.getStandardParameter( OsirisContext.env.USERNAME ) },
    ] as ReportModel;

    def back() {
        return 'default' 
    }

}
